import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import StoreContext from '../components/Store/Context';
import logo from "../img/logo.png";
import './sidebar.css'
/*
import logo from "../img/logo.png";

import Encomendas from '../pages/Encomendas';
import RecursosHumanos from '../pages/RecursosHumanos';
import VendasRealizadas from '../pages/VendasRealizadas';
import PrateleiraseEstoque from '../pages/EstoqueePrateleiras'
import Relatorios from '../pages/Relatorios'
import Departamento from '../pages/Departamento'
import JornadadeTrabalho from '../pages/JornadadeTrabalho'
*/

function Sidebar (userData, page) {
    const navigate = useNavigate();
	const { setToken, token } = useContext(StoreContext);

    return (
        <>
	<div class="sidenav">
        <img
              alt=""
              src={logo}
              width="150"
              height="150"
              className="img-logo"
              />{' '}

		<div class="options">
			<span class="options-title">OPÇÕES</span>
			<div class="options-rest">
				<a class="item-a">
					<span class="item" onClick={(event) => navigate("JornadadeTrabalho",  { replace: false }, {state: {userData: userData}})}>Jornada de Trabalho</span>
				</a>
				<a class="item-a">
					
					<span class="item"onClick={(event) => navigate("RecursosHumanos",  { replace: false }, {state: {userData: userData}})}>Recursos Humanos</span>
				</a>{ token == 3 || token == 1 ?
					<a class="item-a">
				
					<span class="item" onClick={(event) => navigate("VendasRealizadas",  { replace: false }, {state: {userData: userData}})}>Vendas</span>
				</a>
				: ''}
				
				{token == 2 || token == 1 ? <a class="item-a">
					
					<span class="item" onClick={(event) => navigate("Encomendas",  { replace: false }, {state: {userData: userData}})}>Encomendas</span>
				</a> : ''}

				
				{ StoreContext.token == 1 ? <a class="item-a">
					
					<span class="item"onClick={(event) => navigate("Relatorios",  { replace: false }, {state: {userData: userData}})}>Relatórios</span>
				</a> : ''}
				{StoreContext.token == 1 ? <a class="item-a">
					
					<span class="item"onClick={(event) => navigate("Departamento",  { replace: false }, {state: {userData: userData}})}>Funcionários</span>
				</a>: ''}
	
				<a class="item-a">
			<span class="item"onClick={(event) => navigate("Usuario",  { replace: false }, {state: {userData: userData}})}>Usuário</span>
			</a>
			<a class="item-a">
			<span class="item" onClick={() => setToken(null)}>Encerrar</span>
			</a>
			</div>
			
		</div>
		
	</div>

  
      </>
      );
};



export default Sidebar
