import React, { useState, useEffect, useContext } from 'react';
import '../components/style/style.css'

import logo from "../img/logo.png"
import logoSimples from "../img/logoSimples.png"
import StoreContext from '../components/Store/Context';


function createRandomUsers(count = 5) {
  const users = [];
  
  for (let i = 0; i < count; i++) {
    users.push({
      userId: i + 120,
      cpf: "123456789 -0" + i,
      name: "Funcionario " + i,
      funcao : "Operador",
      salario: 1417.25,
      dataInicio: '01-01-2024',
      horaIntervalo: '12:30'
    });
  }

  return users;
}

function Departamento({userData}){
  const [tableData, setTableData] = useState([]);
  const { setToken, token } = useContext(StoreContext);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      /*
      const response = await fetch("https://cities-qd9i.onrender.com/agents");
      const agents = await response.json();
      
      setTableData(agents);
      */
     console.log(tableData);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const handleUpdateUser = () => {
    const newUsers = createRandomUsers()
    setTableData([...tableData, ...newUsers])
  }

  const handleClearUser = () => {
    setTableData([]);
  }

    return (
        <div className="mform">
        <div className = "text">Funcionários</div>
      <input onClick={handleUpdateUser}>Update User</input>
      <button onClick={handleClearUser}>Delete User</button>
      <table style={{width: '100%'}}>
        <thead>
          <tr>
            <th>ID</th>
            <th>CPF</th>
            <th>Funcionário</th>
            <th>Função</th>
            <th>Salário</th>
            <th>Data Início</th>
            <th>Hora de Intervalo</th>
          </tr>
        </thead>
        <tbody>
          {
            tableData.map((obj) => {
              return (
                <tr>
                  <td>{obj.userId}</td>
                  <td>{obj.cpf}</td>
                  <td>{obj.name}</td>
                  <td>{obj.funcao}</td>
                  <td>{obj.salario}</td>
                  <td>{obj.dataInicio}</td>
                  <td>{obj.horaIntervalo}</td>
                </tr>
              );
            })
          }
        </tbody>
      </table>
        </div>
  );
}

export default Departamento;
