import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {withRouter} from 'react-router-dom';
import axios from 'axios';
// import $ from 'jquery';

import logo from "../img/logo.png"
import Popup from '../components/popuplogin';
import '../components/style/style.css'


function RecursosHumanos({userData}){
    //const history = useNavigate();
    const navigate = useNavigate();
    
    const[formData, setChamados] = useState({
        chamado: {
            idFunc: ' ',
            departamento: ' ',
            titulo: ' ',
            assunto: ' '
        }
    });
    
    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setChamados((prevData) => ({
          ...prevData,
          [name]: value,
        }));
    };

    /*
    function sendChamado(input){
      var jqXHR = $.ajax({
          type: "POST",
          url: "/api/sendChamado",
          async: false,
          data: { data: input}
      });
  
      return jqXHR.responseText;
      // return jqXHR;
  }
  */

  const handleSubmit = (event) => {
    event.preventDefault();
    
    /*axios.post('http://127.0.0.1:5000/api/sendDados', formData)
      .then(response => {
        console.log('Resposta do servidor:', response.data);
      })
      .catch(error => {
        console.error('Erro ao enviar dados:', error);
      });*/
  };

    return (
      <div className="main">
        <form onSubmit={handleSubmit}>
           
            <div className='form-all'>
              <div class = "text">
                Abrir Chamado
              </div>
              <div class="form-row">
              <div class="input-data">
                <input 
                        name="idFunc" 
                        className='dadosChamado' 
                        value={formData.chamado.idFunc}
                        onChange={handleInputChange} />
                        <div class="underline"></div>
                    <label>
                    ID Funcionario
                    </label>
              </div>
              <div class="input-data">
                  <input 
                        name="departamento" 
                        className='dadosChamado' 
                        value={formData.chamado.departamento}
                        onChange={handleInputChange} />
                  <div class="underline"></div>
                <label>
                    Departamento
                    
                </label>    
                </div>
                
                <div class = "input-data">
                  <input 
                        name="titulo" 
                        className='dadosChamado' 
                        value={formData.chamado.titulo}
                        onChange={handleInputChange} />
                </div>
                <div class="underline"></div>
                <label>
                    Titulo  
                </label>
              </div>
              <div className='form-row'>
              <div class = "input-data textarea">
                  <input 
                        name="assunto" 
                        className='dadosChamado' 
                        value={formData.chamado.assunto}
                        onChange={handleInputChange} />
                
                </div>
                <div class="underline"></div>
                <label>
                    Assunto
                </label>
                </div>
         
                <button type="submit"> Enviar</button>
                
            </div>
        </form>
        <div className='Chamados'>

        </div>
      </div>
        
        
  );
}

export default RecursosHumanos;
