import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import StoreContext from '../components/Store/Context';
import logo from "../img/logo.png";
import './sidebar.css'
/*
import logo from "../img/logo.png";

import Encomendas from '../pages/Encomendas';
import RecursosHumanos from '../pages/RecursosHumanos';
import VendasRealizadas from '../pages/VendasRealizadas';
import PrateleiraseEstoque from '../pages/EstoqueePrateleiras'
import Relatorios from '../pages/Relatorios'
import Departamento from '../pages/Departamento'
import JornadadeTrabalho from '../pages/JornadadeTrabalho'
*/

function Sidebar (userData) {
    const navigate = useNavigate();
	const { setToken, token } = useContext(StoreContext);

    return (
        <>
	<div class="sidenav">
        <img
              alt=""
              src={logo}
              width="200"
              height="200"
              className="d-inline-block align-top"
              />{' '}

		<div class="options">
			<span class="options-title">OPÇÕES</span>
			<div class="options1">
				<a class="jornada">
					<span class="jornadaspan" onClick={(event) => navigate("JornadadeTrabalho",  { replace: false }, {state: {userData: userData}})}>Jornada de Trabalho</span>
				</a>
				<a class="rh">
					
					<span class="rhspan"onClick={(event) => navigate("RecursosHumanos",  { replace: false }, {state: {userData: userData}})}>Recursos Humanos</span>
				</a>{ token == 3 || token == 1 ?
					<a class="vendas">
				
					<span class="vendasspan" onClick={(event) => navigate("VendasRealizadas",  { replace: false }, {state: {userData: userData}})}>Vendas</span>
				</a>
				: ''}
				
				{token == 2 || token == 1 ? <a class="encomenda">
					
					<span class="encomendaspan" onClick={(event) => navigate("Encomendas",  { replace: false }, {state: {userData: userData}})}>Encomendas</span>
				</a> : ''}
			</div>
			<div class="gerenteclass">
				{ StoreContext.token == 1 ? <a class="relatoriosefunc">
					
					<span class="relatoriospan"onClick={(event) => navigate("Relatorios",  { replace: false }, {state: {userData: userData}})}>Relatórios</span>
				</a> : ''}
				{StoreContext.token == 1 ? <a class="flex items-center w-full h-12 px-3 mt-2 rounded hover:bg-gray-300" href="#">
					
					<span class="funcionariospan"onClick={(event) => navigate("Departamento",  { replace: false }, {state: {userData: userData}})}>Funcionários</span>
				</a>: ''}
	
			</div>
		</div>
		<a class="user">
			<span class="userspan"onClick={(event) => navigate("Usuario",  { replace: false }, {state: {userData: userData}})}>Usuário</span>
		</a>
		<a class="exit">
		<span class="exit-button" onClick={() => setToken(null)}>Encerrar</span>
		</a>
	</div>

  
      </>
      );
};



export default Sidebar
