import React, { useState, useContext, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import StoreContext from '../components/Store/Context';
import logo from "../img/logo.png";
import './sidebar.css'
/*
import logo from "../img/logo.png";

import Encomendas from '../pages/Encomendas';
import RecursosHumanos from '../pages/RecursosHumanos';
import VendasRealizadas from '../pages/VendasRealizadas';
import PrateleiraseEstoque from '../pages/EstoqueePrateleiras'
import Relatorios from '../pages/Relatorios'
import Departamento from '../pages/Departamento'
import JornadadeTrabalho from '../pages/JornadadeTrabalho'
*/

function Sidebar ({userData, newMessage, token}) {
    const navigate = useNavigate();

    return (
        <>
	<div className="sidenav">
        <img
              alt=""
              src={logo}
              width="150"
              height="150"
              className="img-logo"
              />{' '}

		<div className="options">
			<span className="options-title">OPÇÕES</span>
			<div className="options-rest">
				<a className="item-a">
					<span className="item" onClick={(event) => newMessage(event, 1)}>Jornada de Trabalho</span>
				</a>
				<a className="item-a">
					
					<span className="item" onClick={(event) => newMessage(event, 2)}>Recursos Humanos</span>
				</a>{ token == 3 || token == 1 ?
					<a class="item-a">
				
					<span className="item" onClick={(event) => newMessage(event, 3)}>Vendas</span>
				</a>
				: ''}
				
				{token == 2 || token == 1 ? <a class="item-a">
					
					<span className="item" onClick={(event) => newMessage(event, 4)}>Encomendas</span>
				</a> : ''}

				
				{ token == 1 ? <a class="item-a">
					
					<span className="item"onClick={(event) => newMessage(event, 5)}>Relatórios</span>
				</a> : ''}
				{token == 1 ? <a class="item-a">
					
					<span className="item"onClick={(event) => newMessage(event, 6)}>Funcionários</span>
				</a>: ''}
	
				<a class="item-a">
			<span className="item"onClick={(event) => newMessage(event, 7)}>Usuário</span>
			</a>
			<a class="item-a">
			<span className="item" onClick={() => setToken(null)}>Encerrar</span>
			</a>
			</div>
			
		</div>
		
	</div>

  
      </>
      );
};



export default Sidebar
